CREATE TABLE IF NOT EXISTS `tbl_like` (
  `likeid` int(11) NOT NULL AUTO_INCREMENT COMMENT '点赞编号',
  `newsid` int(11) DEFAULT NULL COMMENT '新闻编号',
  `userip` varchar(40) NOT NULL COMMENT 'IP地址',
  PRIMARY KEY (`likeid`),
  KEY `FK_newsid` (`newsid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

-- --------------------------------------------------------

--
-- 表的结构 `tbl_news`
--

CREATE TABLE IF NOT EXISTS `tbl_news` (
  `newsid` int(11) NOT NULL AUTO_INCREMENT COMMENT '新闻编号',
  `title` varchar(100) NOT NULL COMMENT '新闻标题',
  `content` text COMMENT '新闻内容',
  `publishtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布时间',
  `uid` int(11) NOT NULL COMMENT '用户编号',
  `classid` int(11) NOT NULL COMMENT '新闻类型编号',
  `istop` int(11) NOT NULL DEFAULT '0' COMMENT '置顶标志',
  `ishot` int(11) NOT NULL DEFAULT '0' COMMENT '热点标志',
  `likecount` int(11) NOT NULL DEFAULT '0' COMMENT '新闻点赞计数',
  `viewcount` int(11) NOT NULL DEFAULT '0' COMMENT '新闻访问量',
  `newsimg` varchar(50) NOT NULL,
  PRIMARY KEY (`newsid`),
  KEY `FK_uid` (`uid`),
  KEY `FK_classid` (`classid`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=76 ;

