CREATE TABLE IF NOT EXISTS `tbl_reply` (
  `replyid` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论编号',
  `content` varchar(1000) DEFAULT NULL COMMENT '评论内容',
  `publishtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '发布时间',
  `uid` int(11) NOT NULL COMMENT '用户编号',
  `newsid` int(11) DEFAULT NULL COMMENT '用户编号',
  PRIMARY KEY (`replyid`),
  KEY `FK_uid` (`uid`),
  KEY `FK_newsid` (`newsid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

